/**
 * @brief Initialize the MQTT library.
 *
 * @return `EXIT_SUCCESS` if all libraries were successfully initialized;
 * `EXIT_FAILURE` otherwise.
 */
static int _initializeDemo( void )
{
    int status = EXIT_SUCCESS;
    IotMqttError_t mqttInitStatus = IOT_MQTT_SUCCESS;
//    AwsIotShadowError_t shadowInitStatus = AWS_IOT_SHADOW_SUCCESS;

    /* Flags to track cleanup on error. */
    bool mqttInitialized = false;

    /* Initialize LED */
//    init_led();

    /* Initialize Sensor */
//    init_sensors();

    R_IICA0_Create();
    EI();
    ZMOD4410_Reset();
	R_SoftwareDelay(2500, DELAY_UNITS_MILLISECONDS);
	sensors_init(&g_sensors_data);					/* Initialize sensor */

#if 0
	/* debug */
	{
        sensors_t data;
        while(1)
        {
            sensors_temp_hum_read(&data);
	        R_SoftwareDelay(2500, DELAY_UNITS_MILLISECONDS);
        }
	}
	/* debug */
#endif

    /* Initialize the MQTT library. */
    mqttInitStatus = IotMqtt_Init();

    if( mqttInitStatus == IOT_MQTT_SUCCESS )
    {
        mqttInitialized = true;
    }
    else
    {
        status = EXIT_FAILURE;
    }
#if 0
    /* Initialize the Shadow library. */
    if( status == EXIT_SUCCESS )
    {
        /* Use the default MQTT timeout. */
        shadowInitStatus = AwsIotShadow_Init( 0 );

        if( shadowInitStatus != AWS_IOT_SHADOW_SUCCESS )
        {
            status = EXIT_FAILURE;
        }
    }
#endif
    /* Clean up on error. */
    if( status == EXIT_FAILURE )
    {
        if( mqttInitialized == true )
        {
            IotMqtt_Cleanup();
        }
    }

    return status;
}

